import { Nav } from 'react-bootstrap';
import './Sidebar.css';

const Sidebar = () => {
  return (
    <div className="sidebar">
      {/* Header Section */}
      <div className="sidebar-header">
        <h1 className="firms-title">FIRMS</h1>
        <div className="user-profile">
          <h2 className="user-name">Maria</h2>
          <p className="user-role">HR Manager</p>
        </div>
      </div>

      {/* Main Navigation */}
      <div className="dashboard-section">
        <h2 className="section-title">Dashboard</h2>
        <Nav className="flex-column">
          {['Chat', 'Employees', 'Feed', 'Recognition', 'Event', 'Profile', 'Settings'].map((item) => (
            <Nav.Link key={item} className="nav-item">
              {item}
            </Nav.Link>
          ))}
        </Nav>
      </div>

      <div className="divider"></div>

      {/* Free Section */}
      <div className="free-section">
        <h3 className="section-title">Free</h3>
        <Nav className="flex-column">
          {['Allist', '1', '2', '3', '4'].map((item) => (
            <Nav.Link key={item} className="nav-item">
              {item}
            </Nav.Link>
          ))}
        </Nav>
      </div>
    </div>
  );
};

export default Sidebar;
